import AIFlow from '../../src/index';

const blankImg = 'data:image/png;base64,';
const loadingImg = 'data:image/png;base64,';

// 渲染参数
let node1 = {
    // id
    templateName: 'iconTemplate',
    id: '123',
    defineData: {
        nodeName: '123',
        imgSrc: blankImg
    },
    // 画布中位置
    position: [100, 100],
    // 输入
    input: [
    ],
    // 输出
    output: [
    ]
};
let node2 = {
    // id
    id: '124',
    defineData: {
        nodeName: '124',
        imgSrc: loadingImg
    },
    // 画布中位置
    position: [300, 300],
    // 输入
    input: [
    ],
    // 输出
    output: [
    ]
};

let nodes = [
    node1,
    node2
];
let edges = [
];
// 实例化
// 模版配置，主要用于样式
let globalConfig = {
    // 是否静态图片
    isStatic: true,
    // 整个图默认模版
    templateName: 'defaultTemplate'
}
let workflow = new AIFlow(document.getElementById('aw'), {nodes, edges}, globalConfig);
let loadingAni = null;
document.getElementById('loading').addEventListener('click', function () {
    workflow.updateNode('123', 'icon', {
        style: {
            image: loadingImg
        }
    });
    let curNode = workflow.getNodeInsById('123');
    loadingAni = curNode.dom.icon.animate('', true)
        .when(0, {
            rotation: 0
        })
        .when(400, {
            rotation: (Math.PI / 2) * -1
        })
        .when(800, {
            rotation: Math.PI * -1
        })
        .when(1200, {
            rotation: Math.PI * 1.5 * -1
        })
        .when(1600, {
            rotation: Math.PI * 2 * -1
        })
        .start();
});

document.getElementById('stop').addEventListener('click', function () {
    loadingAni && loadingAni.stop();
    loadingAni = null;
    workflow.updateNode('123', 'icon', {
        style: {
            image: ' '
        }
    });
});







